/**
 * Sub-Brands Section Shortcode
 * Creates an "Our sub-brands" section with four sub-brand cards displayed in a 2x2 grid.
 * 
 * Usage: [sub_brands_section]
 */

if (!defined('ABSPATH')) {
    exit; // Exit if accessed directly.
}

function neways_sub_brands_section_shortcode($atts) {
    $atts = shortcode_atts(array(
        'title' => 'Our sub-brands',
        'intro' => 'Four specialized divisions working together to deliver comprehensive, integrated care solutions',
        'background' => 'gradient', // gradient, white, gray
        'class' => ''
    ), $atts);
    
    // Background classes and styles
    $bg_class = '';
    $bg_style = '';
    if ($atts['background'] === 'gradient') {
        $bg_style = 'background: linear-gradient(135deg, #F0FDFA 0%, #EFF6FF 50%, #EEF2FF 100%);';
    } elseif ($atts['background'] === 'gray') {
        $bg_class = 'bg-gray-50';
    } else {
        $bg_class = 'bg-white';
    }
    
    ob_start();
